import { concat, IExternalWrapInstance, u32ToBuffer } from "@nerfzael/reim-wrap-as";
import { send } from "../../dt";

export class HostWrapInstance extends IExternalWrapInstance {
  constructor() {
    super();
  }
  
  invokeResource(resource: u32, buffer: ArrayBuffer): ArrayBuffer {
    const result = send(concat([u32ToBuffer(resource), buffer]));

    return result;
  }
}
